import React from 'react';
import Card from 'react-bootstrap/Card';
import styled from 'styled-components';
import teamData from './team-data';
import { Draggable } from "react-beautiful-dnd";
import logo from './images/miami-dolphins-logo-png-6.png';
import './teamCard.css';

const Container = styled.div`
    margin-bottom: 2px;
    height: 100px;
    background-color: ${props => props.isDragging ? 'blue' : 'white'};
`;

const StyledCard = styled(Card)`


    color: black;
    background-color: #92a8d1;
	font-size: 1em;


	border: 2px solid palevioletred;
	border-radius: 3px;
`;

const ColoredCardBody = styled.div`
    background-color: #92a8d1;
`;


export default class TeamCard extends React.Component {

    constructor(props) {
        super(props)

        console.log(props.value)
        const teamId = props.value
        console.log(props.teams[props.value])

        this.state = {
            id: props.id,
            teamId: props.value,
            teamName: teamData[props.teams[teamId].content],
            //teamLink: null,
            //teamImage: null
        }

        //alert(this.state.teamName)

    }

    //alert(this.props.teamId)

    render() {

        return (
            <Draggable draggableId={this.props.id} index={this.props.index}>
                { (provided, snapshot) => (

                        <StyledCard
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        innerRef={provided.innerRef}
                        ref={provided.innerRef}
                        isDragging={snapshot.isDragging}>
                            <Card.Header> {this.state.teamName}</Card.Header>
                            <ColoredCardBody>
                            <Card.Text> Pick #:{this.props.index + 1} </Card.Text>
                            </ColoredCardBody>
                        </StyledCard>

                )
                }
            </Draggable>
        )
    }

}
