import React, { useState } from 'react';
import styled from 'styled-components';
import teamData from './team-data';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import Form from 'react-bootstrap/Form'
import './TeamModal.css';

const HiddenButton = styled.button`

    background-color: Transparent;
    background-repeat:no-repeat;
    border: none;
    cursor:pointer;
    overflow: hidden;
    outline:none;
`;

const ScrollingDropdownButton = styled(DropdownButton) `
    height: 100px;

`;



export default function TeamModal(props) {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    var state = {
        value: ""
    }
    //console.log(Array(teamData))

    const teams = Array(teamData)[0]

    //console.log(teams)
    var x;
    //for (const property in teamData) {
    //    console.log(teamData[property].name)
    //}

    //teams.map(value => ( console.log(value)))

    //console.log(teamData[0])

    Array(teamData).map(value => {
        //console.log(value)
        //console.log("ok");

    }
    )

    const handleChange = (event) => {
        state = {value: event.target.value}
    }



    const handleSubmit = (event) => {
        alert("Pick Traded");
        event.preventDefault();
    }

    return (
        <>
        <HiddenButton onClick={handleShow}>
            {props.teamName}
        </HiddenButton>

        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Pick #{props.index + 1}</Modal.Title>
            </Modal.Header>
            <Form onSubmit={handleSubmit}>
                <Modal.Body>Choose Traded Team:<br />

                    <Form.Group controlId="exampleForm.ControlSelect2">

                        <Form.Control as="select" onChange={handleChange}>
                            {Object.keys(teamData).map(value => (
                                <option id={value}>{teamData[value].name} </option>
                            ))
                            }
                        </Form.Control>
                    </Form.Group>

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
            </Button>
                    <Button variant="primary" type="submit" value="Submit" onClick={handleSubmit}>
                        Save Changes
            </Button>
                </Modal.Footer>
            </Form>
        </Modal>
        </>
    );

}
