import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Table from 'react-bootstrap/Table'
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import './Mock.css';

const nflTeams = {
    0: "Arizona Cardinals",
    1: "Atlanta Falcons",
    2: "Baltimore Ravens",
    3: "Buffalo Bills",
    4: "Carolina Panthers",
    5: "Chicago Bears",
    6: "Cincinnati Bengals",
    7: "Cleveland Browns",
    8: "Dallas Cowboys",
    9: "Denver Broncos",
    10: "Detroit Lions",
    11: "Green Bay Packers",
    12: "Houston Texans",
    13: "Indianapolis Colts",
    14: "Jacksonville Jaguars",
    15: "Kansas City Chiefs",
    16: "Los Angeles Chargers",
    17: "Los Angeles Rams",
    18: "Miami Dolphins",
    19: "Minnesota Vikings",
    20: "New England Patriots",
    21: "New Orleans Saints",
    22: "New York Giants",
    23: "New York Jets",
    24: "Oakland Raiders",
    25: "Philadelphia Eagles",
    26: "Pittsburgh Steelers",
    27: "San Francisco 49ers",
    28: "Seattle Seahawks",
    29: "Tampa Bay Buccaneers",
    30: "Tennessee Titans",
    31: "Washington Redskins"
}

function DraftCard(props) {


    return (
        <Card className="teamCard">
            <Card.Body> {nflTeams[props.value]} </Card.Body>
        </Card>
    )



}

const reorder = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
};

const getItemStyle = (isDragging, DraggableStyle) => ({
    userSelect: "none",

    // change background colour if dragging
    background: isDragging ? "lightgreen" : "grey"

});

class GenerateList extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            orderTeams: props.value,
            orderPlayers: Array(32).fill(null),
        }

        this.onDragEnd = this.onDragEnd.bind(this);

    }


    onDragEnd(result) {
        // dropped outside the list
        if (!result.destination) {
            return;
        }

        const orderTeams = reorder(
            this.state.orderTeams,
            result.source.index,
            result.destination.index
        );

        this.setState({
            orderTeams
        })
    }


    renderTeamCard(i) {
        return <DraftCard value={i} />;
    }

    render() {

        const currentTeams = this.state.orderTeams.slice()

        return (

            <Table responsive>
                <thead>
                    <tr>
                        <th>Team</th>
                        <th>Player</th>
                    </tr>
                </thead>
                <tbody>
                    {currentTeams.map((value, index) => {
                        return <tr key={index}><th>{this.renderTeamCard(value.content)}</th><th>test</th></tr>
                    })
                    }
                </tbody>
            </Table>

        )
    }

}

function Mock() {


    let defaultOrder = []

    for (var i = 0; i < 32; i++) {
        defaultOrder.push(
            {
                id: 'item-' + i,
                content: i
            }
        )
    }





    return (
        <div className="draftTable">
            <Container>

                <GenerateList value={defaultOrder} />

            </Container>
        </div>
    );
}

export default Mock;
