import React, { useState } from 'react';
import styled from 'styled-components';
import teamData from './team-data';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import ModalBody from 'react-bootstrap/ModalBody';
import Form from 'react-bootstrap/Form';
import DraftCompiler from './DraftCompiler'

const HiddenButton = styled.button`

    background-color: Transparent;
    background-repeat:no-repeat;
    border: none;
    cursor:pointer;
    overflow: hidden;
    outline:none;
    width: auto;

`;

const FullTextArea = styled.textarea`
    width: full;

    color: black;

`;

function testFunc(teams) {

    //console.log(teams.teams)
    return DraftCompiler(teams)

    //Example();
}

export default function ShareModal(props) {

    let draftOutput = ""


    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => {

        setShow(true);

        draftOutput = testFunc(props.teamInfo);
        console.log(draftOutput)
    }

    return (
        <>
        <Button onClick={handleShow} variant="danger">Share</Button>
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton><Modal.Title>Shareable List:</Modal.Title></Modal.Header>
            <Modal.Body>
            <Form.Control as="textarea" rows="3" defaultValue={draftOutput}/>
                {draftOutput}

            </Modal.Body>
        </Modal>

        </>
    );






}
