import React from 'react';
import ReactDOM from 'react-dom';
import teamData from './team-data';

export default function DraftCompiler(draftInfo){

    var text = ""

    draftInfo.ids.map( (value, index) => {

        let newLine = teamData[draftInfo.teams[value].content].name

        let playerId = draftInfo.pickedPlayers[index]

        var chosenPick = "--Not Selected--"

        if(playerId != null){
            chosenPick = draftInfo.players[playerId]
        }

        //let chosenPick =  draftInfo.players[ draftInfo.pickedPlayers[index] ].content



        //console.log(newLine)
        text += "Pick # " + index + ":\t\t\t" + chosenPick + "\nby the " + newLine + "\n";
        //console.log(text)
    })

    //text.current.select();
    //document.execCommand('copy')
    alert('Draft Copied')
    console.log(text)
    return text;



}
