import React, { useState } from 'react';
import styled from 'styled-components';
import teamData from './team-data';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';

const HiddenButton = styled.button`

    background-color: Transparent;
    background-repeat:no-repeat;
    border: none;
    cursor:pointer;
    overflow: hidden;
    outline:none;
`;

export default function TeamModal(props) {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
        <HiddenButton onClick={handleShow}>
            {props.teamName}
        </HiddenButton>

        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Pick #{props.index + 1}</Modal.Title>
            </Modal.Header>
            <Modal.Body>Woohoo, you're reading this text in a modal!<br />
                <DropdownButton id="dropdown-basic-button" title="Dropdown button">
                {teamData.map( (value, index) => ( 
                    <Dropdown.Item href={index}>{value.name}</Dropdown.Item>

                ) ) }

                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                </DropdownButton>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
            </Button>
                <Button variant="primary" onClick={handleClose}>
                    Save Changes
            </Button>
            </Modal.Footer>
        </Modal>
        </>
    );

}
