import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Table from 'react-bootstrap/Table'
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import './Mock.css';
import teamData from './team-data';
import TeamColumn from './TeamColumn';
import PlayerColumn from './PlayerColumn';
import initialData from './initial-data.js';
import styled from 'styled-components';


const DraftTable = styled.div`
    display: flex;
    width: 80%;
    margin: auto;
`;


const reorder = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
};

const getItemStyle = (isDragging, DraggableStyle) => ({
    userSelect: "none",

    // change background colour if dragging
    background: isDragging ? "lightgreen" : "grey",

    ...DraggableStyle

});

const grid = 8;

const getListStyle = isDraggingOver => ({
    background: isDraggingOver ? "lightblue" : "lightgrey",
    padding: grid,
    width: 700

});


class GenerateList extends React.Component {

    render() {
        return <b>Monkey</b>
    }

}



export default class Mock extends React.Component {

    state = initialData;

    sendData = () => {
        this.props.parentCallback({
            ids: this.state.columns['team-column'].teamIds,
            teams: this.state.teams
        });
    }

    constructor(props) {
        super(props)

        let defaultOrder = {};
        let filledTeamIds = []

        for (var i = 0; i < 32; i++) {
            defaultOrder['team-' + i] = (
                {
                    id: 'team-' + i,
                    content: i
                }
            )
            filledTeamIds.push('team-' + i)
        }


        this.state = {
            ...this.state,
            teams: defaultOrder,
            columns: {
                ...this.state.columns,
                ['team-column']: {
                    ...this.state.columns['team-column'],
                    teamIds: filledTeamIds
                }
            }
        }

        this.sendData()

    }

    onDragEnd = result => {
        const { destination, source, draggableId } = result;
        if (!destination) {
            return;
        }

        if (destination.droppableId === source.droppableId && destination.index === source.index) {
            return;
        }

        const column = this.state.columns[source.droppableId];
        const newTeamIds = Array.from(column.teamIds)

        newTeamIds.splice(source.index, 1)
        newTeamIds.splice(destination.index, 0, draggableId)

        const newColumn = {
            ...column,
            teamIds: newTeamIds,
        };

        const newState = {
            ...this.state,
            columns: {
                ...this.state.columns,
                [newColumn.id]: newColumn
            },
        };

        this.setState(newState)

        this.sendData()

    }
    render() {

        return (
            <DragDropContext onDragEnd={this.onDragEnd}>
                <DraftTable>
                    {this.state.columnOrder.map(columnId => {
                        //alert(columnId)
                        const teamColumn = this.state.columns[columnId];

                        //alert(teamColumn.id)
                        if (columnId === 'team-column') {
                            const teams = this.state.teams;
                            const actualTeams = teamColumn.teamIds;
                            //const teamIds = teams.map(teamId => this.state.teams)
                            //teamColumn.teamIds = Array(teams);

                            return <TeamColumn key={teamColumn.id} title="Teams" teamIds={actualTeams} column={teamColumn} teams={teams} />
                        }

                        if(columnId === 'player-column'){
                            const players = this.state.players;
                            const pickedPlayers = teamColumn.playerIds;

                            return <PlayerColumn key={teamColumn.id} playerIds = {pickedPlayers} column={teamColumn} players={players} />
                        }

                    })
                    }
                </DraftTable>

            </DragDropContext>

        );
    }
}
