import React from 'react';
import ReactDOM from 'react-dom';
import teamData from './team-data';

export default function DraftCompiler(draftInfo){

    var text = ""

    draftInfo.ids.map( (value, index) => {

        let newLine = teamData[draftInfo.teams[value].content].name

        let chosenPick = draftInfo.pickedPlayers[index]

        if(chosenPick === null){
            chosenPick = "--Not Selected--"
        }

        //console.log(newLine)
        text += "Pick # " + index + ":\t\t\t" + chosenPick + "\nby the " + newLine + "\n";
        //console.log(text)
    })

    //text.current.select();
    //document.execCommand('copy')
    alert('Draft Copied')
    console.log(text)
    return text;



}
