import React from 'react';
//import Card from 'react-bootstrap/Card';
import styled from 'styled-components';
import { Droppable } from 'react-beautiful-dnd';
import PlayerCard from './playerCard';

const Container = styled.div`

    border: 1px solid lightgrey;
    width: 25%;

    text-align: center;

`;

const Title = styled.div`

    width: auto;
    font-weight: bold;
`;

const PlayerList = styled.div`
    border: black;
    padding: 2px;
    margin: 1%;
    overflow-y: scroll;
    height: 450px;

`;

export default class PlayerPool extends React.Component{

    constructor(props) {
        super(props)
        this.state = {
            id: 'player-pool',
            title: 'Unselected Players',
            playerIds: this.props.playerIds

        }
    }
    render() {


        return (
            <Container>
                <Title>{this.props.column.title}</Title>
                {this.props.column.id}
                <PlayerList>
                <PlayerCard id={'test'} playerId={'test'} players={this.props.players} />
                {this.props.playerIds.map( value => (

                    <PlayerCard id={value} playerId={value} players={this.props.players} />
                ))}
                </PlayerList>
            </Container>

        )

    }

}
