import React, { useState } from 'react';
import styled from 'styled-components';
import teamData from './team-data';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import Form from 'react-bootstrap/Form'
import './TeamModal.css';

const HiddenButton = styled.button`

    background-color: Transparent;
    background-repeat:no-repeat;
    border: none;
    cursor:pointer;
    overflow: hidden;
    outline:none;
`;

const ScrollingDropdownButton = styled(DropdownButton) `
    height: 100px;

`;

export default function TeamModal(props) {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    //console.log(Array(teamData))

    const teams = Array(teamData)[0]

    //console.log(teams)
    var x;
    //for (const property in teamData) {
    //    console.log(teamData[property].name)
    //}

    //teams.map(value => ( console.log(value)))

    //console.log(teamData[0])

    Array(teamData).map(value => {
        //console.log(value)
        //console.log("ok");

    }
    )



    return (
        <>
        <HiddenButton onClick={handleShow}>
            {props.teamName}
        </HiddenButton>

        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Pick #{props.index + 1}</Modal.Title>
            </Modal.Header>
            <Modal.Body>Choose Traded Team:<br />
                <Form>
                    <Form.Group controlId="exampleForm.ControlSelect2">
                        <Form.Label>Example multiple select</Form.Label>
                        <Form.Control as="select" multiple>
                            <option>1</option>
                            <option>2</option>
                            <option>3</option>
                            <option>4</option>
                            <option>5</option>
                        </Form.Control>
                    </Form.Group>
                    <DropdownButton id="dropdown-size-small" title="Dropdown button"
                        bsStyle="default"
                        bsSize="small"
                        style={{ maxHeight: "28px" }}>
                        {Object.keys(teamData).map(value => (
                            <Dropdown.Item id={value}>{teamData[value].name} </Dropdown.Item>
                        ))
                        }
                        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                    </DropdownButton>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Close
            </Button>
                <Button variant="primary" onClick={handleClose}>
                    Save Changes
            </Button>
            </Modal.Footer>
        </Modal>
        </>
    );

}
