import React from 'react';
import Card from 'react-bootstrap/Card';
import styled from 'styled-components';
import teamData from './team-data';
import { Draggable } from "react-beautiful-dnd";
import logo from './images/miami-dolphins-logo-png-6.png';
import './teamCard.css';
import TeamModal from './TeamModal';

const Container = styled.div`
    margin-bottom: 2px;
    height: 100px;
    background-color: ${props => props.isDragging ? 'blue' : 'white'};
`;

const StyledCard = styled(Card) `


    color: white;
    background-color: #92a8d1;
	font-size: 1em;
	border-style: solid;

	border: 4px solid palevioletred;
	border-radius: 3px;
    margin-bottom: 1%;
`;

const ColoredCardBody = styled.div`
    background-color: ${props => props.teamColor};
`;


export default class TeamCard extends React.Component {

    constructor(props) {
        super(props)

        //console.log(props.value)
        const teamId = props.value
        //console.log(props.teams[props.value])

        this.state = {
            id: props.id,
            teamId: props.value,
            teamName: teamData[props.teams[teamId].content].name,
            teamColor: teamData[props.teams[teamId].content].color
            //teamLink: null,
            //teamImage: null
        }


    }

    callbackFunction = (childData) => {
        this.setState({
            ...this.state,
            teamId: childData
        })
    }



    render() {

        return (
            <Draggable draggableId={this.props.id} index={this.props.index}>
                {(provided, snapshot) => (

                    <StyledCard
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        innerRef={provided.innerRef}
                        ref={provided.innerRef}
                        isDragging={snapshot.isDragging}>
                        <Card.Header><TeamModal teamName={this.state.teamName} teamId={this.props.value} 
                        index={this.props.index}
                        parentCallback={this.callbackFunction}>{this.state.teamName}</TeamModal></Card.Header>
                        <ColoredCardBody teamColor={this.state.teamColor}>
                            <Card.Text> Pick #:{this.props.index + 1} </Card.Text>
                        </ColoredCardBody>
                    </StyledCard>
                )
                }
            </Draggable>
        )
    }

}
