import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import DraftCompiler from './DraftCompiler'



function draftText(teams) {

    return DraftCompiler(teams)

}

export default function ShareModal(props) {

    let draftOutput = draftText(props.teamInfo)


    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => {

        setShow(true);

        let allInfo = props.allData;

        let totalIds = []
        let totalTeams = []
        let totalPickedPlayers = []
        let totalPlayers = []

        var i;
        var count = 0;
        for(i = 0; i < allInfo.totalRounds; i++){
            totalIds.concat(allInfo.mocks[i].columns['team-column'].teamIds)
            totalTeams.concat(allInfo.mocks[i].teams)
            totalPickedPlayers.concat(allInfo.mocks[i].columns['player-column'].playerIds)
            totalPlayers.concat(allInfo.mocks[i].players)
        }

        let allRoundInfo = {
            ids: totalIds,
            teams: totalTeams,
            pickedPlayers: totalPickedPlayers,
            players: totalPlayers
        }

        console.log(draftText(allRoundInfo))

        draftOutput = draftText(allRoundInfo);
        console.log(draftOutput)
    }

    return (
        <>
        <Button onClick={handleShow} variant="danger">Share</Button>
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton><Modal.Title>Shareable List:</Modal.Title></Modal.Header>
            <Modal.Body>
            <Form.Control as="textarea" rows="18" defaultValue={draftOutput}>

            </Form.Control>
            </Modal.Body>
        </Modal>

        </>
    );






}
